import React from 'react'
import {CurrentUserContext} from '../contexts/CurrentUserContext'

function Card({ card, onCardClick }) {
    const currentUser = React.useContext(CurrentUserContext);
    // Определяем, являемся ли мы владельцем текущей карточки
    const isOwn = card.owner._id === currentUser._id;
    // Создаём переменную, которую после зададим в `className` для кнопки удаления
    const cardDeleteButtonClassName = ( 
        `element__delete-button ${isOwn ? '' : 'element__delete-button_type_hidden'}`
        );
    // Определяем, есть ли у карточки лайк, поставленный текущим пользователем
    const isLiked = card.likes.some(i => i._id === currentUser._id);
    // Создаём переменную, которую после зададим в `className` для кнопки лайка
    const cardLikeButtonClassName = (
        `element__like-button ${isLiked ? 'element__like-button_active' : ''}`
        );

    function handleCardLike(card) {
        // Снова проверяем, есть ли уже лайк на этой карточке
        const isLiked = card.likes.some(i => i._id === currentUser._id);
        // Отправляем запрос в API и получаем обновлённые данные карточки
        api.setLike(card._id, !isLiked).then((newCard) => {
            setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
        });
    }

    function handleClick() {
        onCardClick(card);
    }

    return (
        <article className="element">
            <button aria-label="Удалить карточку" type="button" className={cardDeleteButtonClassName} />
            <img onClick={handleClick} className="element__image" src={card.link} alt={`Крупным планом: ${card.name}`} />
            <div className="element__place">
                <h2 className="element__title">{card.name}</h2>
                <div className="element__like-area">
                    <button aria-label="Оцените фотографию!" type="button" className={cardLikeButtonClassName} />
                    <span className="element__like-count">{card.likes.length}</span>
                </div>
            </div>
        </article>
    )
}

export default Card
